import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Inicio = () => {
    const [tareas, setTareas] = useState([]);
    const [pendientes, setPendientes] = useState(0);
    const [completadas, setCompletadas] = useState(0);

    useEffect(() => {
        axios.get('http://localhost:3001/tareas')
            .then(response => {
                setTareas(response.data);
                setPendientes(response.data.filter(tarea => !tarea.completada).length);
                setCompletadas(response.data.filter(tarea => tarea.completada).length);
            })
            .catch(err => console.error('Error al obtener los datos', err));
    }, []);

    return (
        <div>
            <h1>Resumen de Tareas</h1>
            <p>Tareas Pendientes: {pendientes}</p>
            <p>Tareas Completadas: {completadas}</p>
        </div>
    );
};

export default Inicio;
