import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from 'react-router-dom';
import { FaHome } from 'react-icons/fa'; // Importar el icono de casa

const ListaTareas = () => {
    const [tareas, setTareas] = useState([]);

    const obtenerTareas = () => {
        axios.get("http://localhost:3001/tareas")
            .then(response => setTareas(response.data))
            .catch(err => console.error("Error al obtener los datos", err));
    };

    useEffect(() => {
        obtenerTareas();
    }, []);

    const marcarComoTareaCompletada = (id) => {
        axios.put(`http://localhost:3001/tareas/${id}`)
            .then(response => {
                setTareas(tareas.map(tarea =>
                    tarea.id === id ? { ...tarea, completada: true } : tarea
                ));
            })
            .catch(error => console.error("Error al marcar como completada", error));
    };

    const eliminarTarea = (id) => {
        axios.delete(`http://localhost:3001/tareas/${id}`)
            .then(response => {
                setTareas(tareas.filter(tarea => tarea.id !== id));
            })
            .catch(error => console.error("Error al eliminar tarea", error));
    };

    return (
        <div style={{ textAlign: 'center' }}>
            <Link to="/">
                <button style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    padding: '10px 20px',
                    fontSize: '20px',
                    marginBottom: '20px'
                }}>
                    <FaHome style={{ marginRight: '10px' }} /> Inicio
                </button>
            </Link>
            <h1>Lista de tareas</h1>
            <table style={{ margin: '0 auto', borderCollapse: 'collapse', width: '80%' }}>
                <thead>
                    <tr>
                        <th style={{ border: '1px solid black', padding: '10px' }}>Nombre</th>
                        <th style={{ border: '1px solid black', padding: '10px' }}>Descripción</th>
                        <th style={{ border: '1px solid black', padding: '10px' }}>Tipo</th>
                        <th style={{ border: '1px solid black', padding: '10px' }}>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {tareas.filter(tarea => !tarea.completada).map(tarea => (
                        <tr key={tarea.id}>
                            <td style={{ border: '1px solid black', padding: '10px' }}>{tarea.nombre}</td>
                            <td style={{ border: '1px solid black', padding: '10px' }}>{tarea.descripcion}</td>
                            <td style={{ border: '1px solid black', padding: '10px' }}>{tarea.tipo}</td>
                            <td style={{ border: '1px solid black', padding: '10px' }}>
                                <button onClick={() => marcarComoTareaCompletada(tarea.id)} style={{ marginRight: '10px' }}>Completar</button>
                                <button onClick={() => eliminarTarea(tarea.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <Link to="/agregar-tarea">
                <button style={{
                    position: 'fixed',
                    bottom: '20px',
                    right: '20px',
                    borderRadius: '50%',
                    width: '60px',
                    height: '60px',
                    fontSize: '30px'
                }}>+</button>
            </Link>
        </div>
    );
};

export default ListaTareas;
