import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from 'react-router-dom';
import { FaHome } from 'react-icons/fa'; // Importar el icono de casa
import './ListaTareas.css';

const ListaTareas = () => {
    const [tareas, setTareas] = useState([]);

    const obtenerTareas = () => {
        axios.get("http://localhost:3001/tareas")
            .then(response => setTareas(response.data))
            .catch(err => console.error("Error al obtener los datos", err));
    };

    useEffect(() => {
        obtenerTareas();
    }, []);

    const marcarComoTareaCompletada = (id) => {
        axios.put(`http://localhost:3001/tareas/${id}`)
            .then(response => {
                obtenerTareas();
            })
            .catch(error => console.error("Error al marcar como completada", error));
    };

    const eliminarTarea = (id) => {
        axios.delete(`http://localhost:3001/tareas/${id}`)
            .then(response => {
                obtenerTareas();
            })
            .catch(error => console.error("Error al eliminar tarea", error));
    };

    return (
        <div className="container">
            <Link to="/">
                <button className="btn btn-primary my-4">
                    <FaHome className="mr-2" /> Inicio
                </button>
            </Link>
            <div className="header d-flex justify-content-between align-items-center">
                <h1>Lista de tareas</h1>
                <Link to="/agregar-tarea">
                    <button className="btn btn-primary">+</button>
                </Link>
            </div>
            <table className="table table-striped my-4">
                <thead>
                    <tr>
                        <th>Nombre</th>
                        <th>Descripci√≥n</th>
                        <th>Tipo</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {tareas.filter(tarea => !tarea.completada).map(tarea => (
                        <tr key={tarea.id}>
                            <td>{tarea.nombre}</td>
                            <td>{tarea.descripcion}</td>
                            <td>{tarea.tipo}</td>
                            <td>
                                <button onClick={() => marcarComoTareaCompletada(tarea.id)} className="btn btn-success mr-2">Completar</button>
                                <button onClick={() => eliminarTarea(tarea.id)} className="btn btn-danger">Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ListaTareas;
