import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AgregarTarea = () => {
    const [nombre, setNombre] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [tipo, setTipo] = useState('');
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        const nuevaTarea = { nombre, descripcion, tipo };
        axios.post('http://localhost:3001/tareas', nuevaTarea)
            .then(response => {
                console.log('Tarea agregada:', response.data);
                setNombre('');
                setDescripcion('');
                setTipo('');
                navigate('/lista-tareas'); // Redirigir a la lista de tareas
            })
            .catch(error => console.error('Error al agregar tarea', error));
    };

    const handleDescripcionChange = (e) => {
        setDescripcion(e.target.value);
        e.target.style.height = 'auto';
        e.target.style.height = `${e.target.scrollHeight}px`;
    };

    return (
        <div style={{ textAlign: 'center' }}>
            <h1>Agregar Tarea</h1>
            <form onSubmit={handleSubmit} style={{ display: 'inline-block', textAlign: 'left' }}>
                <div>
                    <label>Nombre:</label>
                    <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} required />
                </div>
                <div>
                    <label>Descripci√≥n:</label>
                    <textarea
                        value={descripcion}
                        onChange={handleDescripcionChange}
                        required
                        style={{ width: '100%', boxSizing: 'border-box', overflow: 'hidden' }}
                    />
                </div>
                <div>
                    <label>Tipo:</label>
                    <input type="text" value={tipo} onChange={(e) => setTipo(e.target.value)} required />
                </div>
                <button type="submit" style={{ display: 'block', margin: '20px auto' }}>Agregar Tarea</button>
            </form>
        </div>
    );
};

export default AgregarTarea;
